void loc_err(const YYLTYPE* loc, const m_str filename) {
  gw_err("\033[1m%s:%u:%u:\033[0m\n", filename, loc->first_line, loc->first_column);
  int n = 1;
  size_t len = 0;
  FILE* f = fopen(filename, "r");
  if(!f)
    return;
  fseek(f, 0, SEEK_SET);
  m_str line = NULL;
  ssize_t sz;
  while((sz = getline(&line, &len, f)) != -1) {
    if(n > loc->last_line)
      break;
    if(n >= loc->first_line) {
      int pos = 0;
      if(n == loc->first_line) {
        while(pos < loc->first_column - 1)
          gw_err("%c", line[pos++]);
        gw_err("\033[4m");
      }
      if(n == loc->last_line) {
        do gw_err("%c", line[pos]);
        while(++pos < loc->last_column - 1);
        gw_err("\033[0m");
      }
      do gw_err("%c", line[pos]);
      while(++pos <= sz);
    }
    n++;
  }
  fclose(f);
  free(line);
}

